import React from 'react'
import App from '../App'

export default function Form(title, settitle, desc, setdesc) {
    const inputHandler=(e)=>{
        if(e.target.id==title){
           settitle(e.target.value);
        }
        else{
            setdesc(e.target.value);
        }
    }
    return (
        <>
            <div className="container my-3">
                <div className="row justify-content-center">
                    <div className="col-md-10">
                        <form style={{border:"2px solid #ced4da", borderRadius:"10px", padding:"30px"}}>
                            <div className="mb-3">
                                <label for="exampleInputEmail1" className="form-label">Title</label>
                                <input type="email" className="form-control" id="title" placeholder='Enter your Title' value={title} onChange={inputHandler}/>
                            </div>
                            <div className="mb-3">
                                <label for="exampleInputPassword1" className="form-label">Description</label>
                                <textarea name="desc" id="desc" rows="3" className="form-control" placeholder='Enter your description' value={desc} onChange={inputHandler}></textarea>
                            </div>

                            <button type="submit" className="btn btn-primary">Add Notes</button>
                        </form>
                    </div>
                </div>
            </div>
        </>
    )
}
